import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import {
  FaTachometerAlt,
  FaList,
  FaChartBar,
  FaUsers,
  FaPlus,
  FaPlay,
  FaDesktop,
  FaUser,
  FaCog,
  FaSearch,
  FaFilter,
  FaTimes,
  FaExclamationTriangle,
  FaCheckCircle,
  FaClock,
  FaPause,
  FaUserCheck,
  FaEnvelope,
  FaBuilding
} from 'react-icons/fa';
import './App.css';

const socket = io('http://localhost:5001');

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [loginError, setLoginError] = useState('');
  const [tickets, setTickets] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ subject: '', description: '', from: '' });
  const [isSharing, setIsSharing] = useState(false);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const [peerConnection, setPeerConnection] = useState(null);
  const [isControlling, setIsControlling] = useState(false);
  const [activeView, setActiveView] = useState('board');
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [users, setUsers] = useState([]);
  const [showUserForm, setShowUserForm] = useState(false);
  const [userFormData, setUserFormData] = useState({ name: '', email: '', role: '', department: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    fetchTickets();
    fetchUsers();

    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);

    return () => {
      socket.off('offer', handleOffer);
      socket.off('answer', handleAnswer);
      socket.off('ice-candidate', handleIceCandidate);
    };
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/tickets');
      setTickets(response.data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const updateTicket = async (id, updates) => {
    try {
      await axios.put(`http://localhost:5001/api/tickets/${id}`, updates);
      fetchTickets();
    } catch (error) {
      console.error('Error updating ticket:', error);
    }
  };

  const createTicket = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/tickets', formData);
      setFormData({ subject: '', description: '', from: '', assignedTo: '' });
      setShowForm(false);
      fetchTickets();
    } catch (error) {
      console.error('Error creating ticket:', error);
    }
  };

  const createUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/users', userFormData);
      setUserFormData({ name: '', email: '', role: '', department: '' });
      setShowUserForm(false);
      fetchUsers();
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const startScreenShare = async (ticketId) => {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: false
      });
      setLocalStream(stream);
      setIsSharing(true);

      socket.emit('join-support', ticketId);

      // Create peer connection with data channel for remote control
      const pc = new RTCPeerConnection();
      setPeerConnection(pc);

      // Add data channel for remote control
      const dataChannel = pc.createDataChannel('remote-control');
      dataChannel.onopen = () => {
        console.log('Data channel opened for remote control');
        setIsControlling(true);
      };
      dataChannel.onmessage = (event) => {
        // Handle incoming control commands from support
        const command = JSON.parse(event.data);
        handleRemoteControlCommand(command);
      };

      // Add video track
      stream.getTracks().forEach(track => pc.addTrack(track, stream));

      pc.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit('ice-candidate', { ticketId, candidate: event.candidate });
        }
      };

      pc.ontrack = (event) => {
        setRemoteStream(event.streams[0]);
      };

      // Handle incoming data channels from support
      pc.ondatachannel = (event) => {
        const channel = event.channel;
        channel.onmessage = (e) => {
          const command = JSON.parse(e.data);
          handleRemoteControlCommand(command);
        };
      };

      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit('offer', { ticketId, offer });
    } catch (error) {
      console.error('Error starting screen share:', error);
    }
  };

  const handleOffer = async (data) => {
    const peerConnection = new RTCPeerConnection();
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', { ticketId: data.ticketId, candidate: event.candidate });
      }
    };
    peerConnection.ontrack = (event) => {
      setRemoteStream(event.streams[0]);
    };

    await peerConnection.setRemoteDescription(new RTCSessionDescription(data.offer));
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);
    socket.emit('answer', { ticketId: data.ticketId, answer });
  };

  const handleAnswer = (data) => {
    // Handle answer from support
  };

  const handleIceCandidate = (data) => {
    // Handle ICE candidates
  };

  const getPriorityIcon = (priority) => {
    switch(priority?.toLowerCase()) {
      case 'high': return <FaExclamationTriangle style={{color: '#FF5630'}} />;
      case 'medium': return <FaExclamationTriangle style={{color: '#FFAB00'}} />;
      case 'low': return <FaExclamationTriangle style={{color: '#36B37E'}} />;
      default: return <FaExclamationTriangle style={{color: '#6B778C'}} />;
    }
  };

  const getStatusColor = (status) => {
    switch(status?.toLowerCase()) {
      case 'open': return '#0052CC';
      case 'in-progress': return '#FF8B00';
      case 'resolved': return '#00875A';
      case 'closed': return '#6B778C';
      default: return '#6B778C';
    }
  };

  const filteredTickets = tickets.filter(ticket =>
    ticket.subject?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.from?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.assignedName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    `LUX-${ticket.id}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleRemoteControlCommand = (command) => {
    // Note: Due to browser security restrictions, true remote control is limited.
    // This demonstrates the concept but may not work perfectly.
    // For production remote control, consider native applications or specialized tools.

    console.log('Received remote control command:', command);

    switch (command.type) {
      case 'mousemove':
        // Simulate mouse movement (limited browser support)
        break;
      case 'mousedown':
        // Simulate mouse click (limited browser support)
        break;
      case 'mouseup':
        // Simulate mouse release (limited browser support)
        break;
      case 'keydown':
        // Simulate key press (limited browser support)
        break;
      case 'keyup':
        // Simulate key release (limited browser support)
        break;
      default:
        console.log('Unknown command type:', command.type);
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (loginData.username === 'Admin' && loginData.password === '#Admin123') {
      setIsAuthenticated(true);
      setLoginError('');
    } else {
      setLoginError('Invalid username or password');
    }
  };

  const groupTicketsByStatus = () => {
    const baseTickets = searchTerm ? filteredTickets : tickets;
    const groups = {
      'To Do': baseTickets.filter(t => t.status === 'open'),
      'In Progress': baseTickets.filter(t => t.status === 'in-progress'),
      'Done': baseTickets.filter(t => t.status === 'resolved' || t.status === 'closed')
    };
    return groups;
  };

  if (!isAuthenticated) {
    return (
      <div className="login-container">
        <div className="login-form">
          <div className="login-header">
            <h2><FaTachometerAlt /> Lux Service</h2>
            <p>Please sign in to access the ticketing system</p>
          </div>
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                placeholder="Enter username"
                value={loginData.username}
                onChange={(e) => setLoginData({...loginData, username: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                placeholder="Enter password"
                value={loginData.password}
                onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                required
              />
            </div>
            {loginError && (
              <div className="error-message">
                {loginError}
              </div>
            )}
            <button type="submit" className="login-btn">Sign In</button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="jira-app">
      <div className={`jira-sidebar ${sidebarOpen ? 'sidebar-open' : ''}`}>
        <div className="sidebar-header">
          <h2><FaTachometerAlt /> Lux Service</h2>
        </div>
        <nav className="sidebar-nav">
          <div className={`nav-item ${activeView === 'board' ? 'active' : ''}`} onClick={() => setActiveView('board')}>
            <FaTachometerAlt /> Board
          </div>
          <div className={`nav-item ${activeView === 'list' ? 'active' : ''}`} onClick={() => setActiveView('list')}>
            <FaList /> List
          </div>
          <div className={`nav-item ${activeView === 'reports' ? 'active' : ''}`} onClick={() => setActiveView('reports')}>
            <FaChartBar /> Reports
          </div>
          <div className={`nav-item ${activeView === 'users' ? 'active' : ''}`} onClick={() => setActiveView('users')}>
            <FaUsers /> Users
          </div>
        </nav>
        <div className="sidebar-footer">
          <button className="create-btn" onClick={() => setShowForm(!showForm)}>
            <FaPlus /> Create Issue
          </button>
          <button className="create-btn" style={{marginTop: '8px', background: '#36b37e'}} onClick={() => setShowUserForm(!showUserForm)}>
            <FaPlus /> Add User
          </button>
        </div>
      </div>

      <div className="jira-main">
        <div className="main-header">
          <div className="header-left">
            <button className="hamburger-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
              <span></span>
              <span></span>
              <span></span>
            </button>
            <h1>Service Desk</h1>
          </div>
          <div className="header-search">
            <div className="search-container">
              <FaSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search issues..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              {searchTerm && (
                <button
                  className="clear-search"
                  onClick={() => setSearchTerm('')}
                >
                  <FaTimes />
                </button>
              )}
            </div>
          </div>
          <div className="header-actions">
            <button className="btn-secondary"><FaCog /> Settings</button>
            <button className="btn-secondary"><FaUser /> Profile</button>
          </div>
        </div>

        {showForm && (
          <div className="modal-overlay" onClick={() => setShowForm(false)}>
            <div className="create-issue-modal" onClick={e => e.stopPropagation()}>
              <div className="modal-header">
                <h3>Create Issue</h3>
                <button className="close-btn" onClick={() => setShowForm(false)}></button>
              </div>
              <form onSubmit={createTicket} className="issue-form">
                <div className="form-group">
                  <label>Summary *</label>
                  <input
                    type="text"
                    placeholder="What needs to be done?"
                    value={formData.subject}
                    onChange={(e) => setFormData({...formData, subject: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Description</label>
                  <textarea
                    placeholder="Add a description..."
                    value={formData.description}
                    onChange={(e) => setFormData({...formData, description: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Reporter *</label>
                  <input
                    type="email"
                    placeholder="user@example.com"
                    value={formData.from}
                    onChange={(e) => setFormData({...formData, from: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Assign To</label>
                  <select
                    value={formData.assignedTo || ''}
                    onChange={(e) => setFormData({...formData, assignedTo: e.target.value})}
                  >
                    <option value="">Auto-assign based on keywords</option>
                    {users.map(user => (
                      <option key={user.id} value={user.id}>{user.name} ({user.role})</option>
                    ))}
                  </select>
                </div>
                <div className="modal-actions">
                  <button type="button" className="btn-secondary" onClick={() => setShowForm(false)}>Cancel</button>
                  <button type="submit" className="btn-primary">Create</button>
                </div>
              </form>
            </div>
          </div>
        )}

        {showUserForm && (
          <div className="modal-overlay" onClick={() => setShowUserForm(false)}>
            <div className="create-issue-modal" onClick={e => e.stopPropagation()}>
              <div className="modal-header">
                <h3>Add New User</h3>
                <button className="close-btn" onClick={() => setShowUserForm(false)}></button>
              </div>
              <form onSubmit={createUser} className="issue-form">
                <div className="form-group">
                  <label>Name *</label>
                  <input
                    type="text"
                    placeholder="Full Name"
                    value={userFormData.name}
                    onChange={(e) => setUserFormData({...userFormData, name: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    placeholder="user@company.com"
                    value={userFormData.email}
                    onChange={(e) => setUserFormData({...userFormData, email: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Role *</label>
                  <select
                    value={userFormData.role}
                    onChange={(e) => setUserFormData({...userFormData, role: e.target.value})}
                    required
                  >
                    <option value="">Select Role</option>
                    <option value="SAP Support">SAP Support</option>
                    <option value="Tableau Support">Tableau Support</option>
                    <option value="Apps Support">Apps Support</option>
                    <option value="IT Support">IT Support</option>
                    <option value="Admin">Admin</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Department *</label>
                  <input
                    type="text"
                    placeholder="IT, Finance, HR, etc."
                    value={userFormData.department}
                    onChange={(e) => setUserFormData({...userFormData, department: e.target.value})}
                    required
                  />
                </div>
                <div className="modal-actions">
                  <button type="button" className="btn-secondary" onClick={() => setShowUserForm(false)}>Cancel</button>
                  <button type="submit" className="btn-primary">Add User</button>
                </div>
              </form>
            </div>
          </div>
        )}

        <div className="main-content">
          {activeView === 'board' && (
            <div className="kanban-board">
              {Object.entries(groupTicketsByStatus()).map(([status, statusTickets]) => (
                <div key={status} className="kanban-column">
                  <div className="column-header">
                    <h3>{status}</h3>
                    <span className="ticket-count">{statusTickets.length}</span>
                  </div>
                  <div className="column-content">
                    {statusTickets.map(ticket => (
                      <div key={ticket.id} className="jira-ticket-card" onClick={() => setSelectedTicket(ticket)}>
                        <div className="ticket-header">
                          <span className="ticket-id">LUX-{ticket.id}</span>
                          <span className="priority-icon">{getPriorityIcon(ticket.priority)}</span>
                        </div>
                        <h4 className="ticket-title">{ticket.subject}</h4>
                        <div className="ticket-meta">
                          <span className="assignee"><FaUserCheck /> {ticket.assignedName}</span>
                          <span className="reporter"><FaEnvelope /> {ticket.from}</span>
                        </div>
                        <div className="ticket-actions">
                          <button className="action-btn" onClick={(e) => { e.stopPropagation(); updateTicket(ticket.id, { status: 'in-progress' }); }}><FaPlay /></button>
                          <button className="action-btn" onClick={(e) => { e.stopPropagation(); startScreenShare(ticket.id); }}><FaDesktop /></button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}

          {activeView === 'list' && (
            <div className="ticket-list">
              <div className="list-header">
                <div className="list-filters">
                  <select className="filter-select">
                    <option>All Issues</option>
                    <option>My Issues</option>
                    <option>Recently Updated</option>
                  </select>
                </div>
              </div>
              <table className="tickets-table">
                <thead>
                  <tr>
                    <th>Key</th>
                    <th>Summary</th>
                    <th>Priority</th>
                    <th>Status</th>
                    <th>Assignee</th>
                    <th>Reporter</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {(searchTerm ? filteredTickets : tickets).map(ticket => (
                    <tr key={ticket.id} className="ticket-row" onClick={() => setSelectedTicket(ticket)}>
                      <td className="ticket-key">LUX-{ticket.id}</td>
                      <td className="ticket-summary">{ticket.subject}</td>
                      <td className="ticket-priority">{getPriorityIcon(ticket.priority)} {ticket.priority}</td>
                      <td className="ticket-status">
                        <span className="status-badge" style={{backgroundColor: getStatusColor(ticket.status)}}>
                          {ticket.status}
                        </span>
                      </td>
                      <td className="ticket-assignee">{ticket.assignedName}</td>
                      <td className="ticket-reporter">{ticket.from}</td>
                      <td className="ticket-actions">
                        <button className="action-btn" onClick={(e) => { e.stopPropagation(); updateTicket(ticket.id, { status: 'in-progress' }); }}><FaPlay /></button>
                        <button className="action-btn" onClick={(e) => { e.stopPropagation(); startScreenShare(ticket.id); }}><FaDesktop /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {activeView === 'reports' && (
            <div className="reports-view">
              <h2><FaChartBar /> Reports & Analytics</h2>
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Total Issues</h3>
                  <div className="stat-number">{tickets.length}</div>
                </div>
                <div className="stat-card">
                  <h3>Open Issues</h3>
                  <div className="stat-number">{tickets.filter(t => t.status === 'open').length}</div>
                </div>
                <div className="stat-card">
                  <h3>In Progress</h3>
                  <div className="stat-number">{tickets.filter(t => t.status === 'in-progress').length}</div>
                </div>
                <div className="stat-card">
                  <h3>Resolved</h3>
                  <div className="stat-number">{tickets.filter(t => t.status === 'resolved').length}</div>
                </div>
              </div>
            </div>
          )}

          {activeView === 'users' && (
            <div className="users-view">
              <div className="users-header">
                <h2><FaUsers /> User Management</h2>
                <button className="btn-primary" onClick={() => setShowUserForm(true)}><FaPlus /> Add User</button>
              </div>
              <div className="users-grid">
                {users.map(user => (
                  <div key={user.id} className="user-card">
                    <div className="user-avatar">
                      {user.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <div className="user-info">
                      <h4>{user.name}</h4>
                      <p className="user-role">{user.role}</p>
                      <p className="user-email">{user.email}</p>
                      <p className="user-department">{user.department}</p>
                    </div>
                    <div className="user-stats">
                      <div className="stat">
                        <span className="stat-number">{tickets.filter(t => t.assignedTo === user.id).length}</span>
                        <span className="stat-label">Assigned</span>
                      </div>
                      <div className="stat">
                        <span className="stat-number">{tickets.filter(t => t.assignedTo === user.id && t.status === 'resolved').length}</span>
                        <span className="stat-label">Resolved</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {selectedTicket && (
          <div className="modal-overlay" onClick={() => setSelectedTicket(null)}>
            <div className="ticket-detail-modal" onClick={e => e.stopPropagation()}>
              <div className="modal-header">
                <h3>LUX-{selectedTicket.id}</h3>
                <button className="close-btn" onClick={() => setSelectedTicket(null)}></button>
              </div>
              <div className="ticket-detail-content">
                <h2>{selectedTicket.subject}</h2>
                <div className="ticket-meta-detail">
                  <div className="meta-item">
                    <strong>Status:</strong>
                    <span className="status-badge" style={{backgroundColor: getStatusColor(selectedTicket.status)}}>
                      {selectedTicket.status}
                    </span>
                  </div>
                  <div className="meta-item">
                    <strong>Priority:</strong> {getPriorityIcon(selectedTicket.priority)} {selectedTicket.priority}
                  </div>
                  <div className="meta-item">
                    <strong>Assignee:</strong> {selectedTicket.assignedName}
                  </div>
                  <div className="meta-item">
                    <strong>Reassign:</strong>
                    <select
                      value={selectedTicket.assignedTo}
                      onChange={(e) => updateTicket(selectedTicket.id, { assignedTo: e.target.value })}
                      style={{marginLeft: '8px', padding: '4px'}}
                    >
                      {users.map(user => (
                        <option key={user.id} value={user.id}>{user.name}</option>
                      ))}
                    </select>
                  </div>
                  <div className="meta-item">
                    <strong>Reporter:</strong> {selectedTicket.from}
                  </div>
                </div>
                <div className="ticket-description">
                  <h4>Description</h4>
                  <p>{selectedTicket.userRequest || selectedTicket.description}</p>
                </div>
                <div className="ticket-actions-detail">
                  <button className="btn-primary" onClick={() => updateTicket(selectedTicket.id, { status: 'in-progress' })}>Start Progress</button>
                  <button className="btn-secondary" onClick={() => startScreenShare(selectedTicket.id)}>Screen Share</button>
                  <button className="btn-secondary" onClick={() => updateTicket(selectedTicket.id, { status: 'resolved' })}>Resolve</button>
                </div>
              </div>
            </div>
          </div>
        )}

        {isSharing && (
          <div className="screen-share-panel">
            <div className="panel-header">
              <h3><FaDesktop /> Screen Sharing Session</h3>
              <button onClick={() => setIsSharing(false)}><FaTimes /></button>
            </div>
            <div className="video-container">
              <video ref={video => video && (video.srcObject = localStream)} autoPlay muted />
              {remoteStream && <video ref={video => video && (video.srcObject = remoteStream)} autoPlay />}
            </div>
            <div className="control-notice">
              <p><strong>Note:</strong> Due to browser security restrictions, remote control functionality is limited. For full remote control capabilities, consider using dedicated remote desktop software.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
