import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import {
  FaTachometerAlt,
  FaList,
  FaChartBar,
  FaUsers,
  FaPlus,
  FaPlay,
  FaDesktop,
  FaUser,
  FaCog,
  FaSearch,
  FaFilter,
  FaTimes,
  FaExclamationTriangle,
  FaCheckCircle,
  FaClock,
  FaPause,
  FaUserCheck,
  FaEnvelope,
  FaBuilding,
  FaBars,
  FaSignOutAlt
} from 'react-icons/fa';
import './App.css';

const socket = io('http://localhost:5001');

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [loginError, setLoginError] = useState('');
  const [tickets, setTickets] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ subject: '', description: '', from: '' });
  const [isSharing, setIsSharing] = useState(false);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const [peerConnection, setPeerConnection] = useState(null);
  const [isControlling, setIsControlling] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [users, setUsers] = useState([]);
  const [showUserForm, setShowUserForm] = useState(false);
  const [userFormData, setUserFormData] = useState({ name: '', email: '', role: '', department: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  useEffect(() => {
    fetchTickets();
    fetchUsers();

    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);

    return () => {
      socket.off('offer', handleOffer);
      socket.off('answer', handleAnswer);
      socket.off('ice-candidate', handleIceCandidate);
    };
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/tickets');
      setTickets(response.data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const updateTicket = async (id, updates) => {
    try {
      await axios.put(`http://localhost:5001/api/tickets/${id}`, updates);
      fetchTickets();
    } catch (error) {
      console.error('Error updating ticket:', error);
    }
  };

  const createTicket = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/tickets', formData);
      setFormData({ subject: '', description: '', from: '', assignedTo: '' });
      setShowForm(false);
      fetchTickets();
    } catch (error) {
      console.error('Error creating ticket:', error);
    }
  };

  const createUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/users', userFormData);
      setUserFormData({ name: '', email: '', role: '', department: '' });
      setShowUserForm(false);
      fetchUsers();
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const startScreenShare = async (ticketId) => {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: false
      });
      setLocalStream(stream);
      setIsSharing(true);

      socket.emit('join-support', ticketId);

      const pc = new RTCPeerConnection();
      setPeerConnection(pc);

      const dataChannel = pc.createDataChannel('remote-control');
      dataChannel.onopen = () => {
        console.log('Data channel opened for remote control');
        setIsControlling(true);
      };
      dataChannel.onmessage = (event) => {
        const command = JSON.parse(event.data);
        handleRemoteControlCommand(command);
      };

      stream.getTracks().forEach(track => pc.addTrack(track, stream));

      pc.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit('ice-candidate', { ticketId, candidate: event.candidate });
        }
      };

      pc.ontrack = (event) => {
        setRemoteStream(event.streams[0]);
      };

      pc.ondatachannel = (event) => {
        const channel = event.channel;
        channel.onmessage = (e) => {
          const command = JSON.parse(e.data);
          handleRemoteControlCommand(command);
        };
      };

      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit('offer', { ticketId, offer });
    } catch (error) {
      console.error('Error starting screen share:', error);
    }
  };

  const handleOffer = async (data) => {
    const peerConnection = new RTCPeerConnection();
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', { ticketId: data.ticketId, candidate: event.candidate });
      }
    };
    peerConnection.ontrack = (event) => {
      setRemoteStream(event.streams[0]);
    };

    await peerConnection.setRemoteDescription(new RTCSessionDescription(data.offer));
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);
    socket.emit('answer', { ticketId: data.ticketId, answer });
  };

  const handleAnswer = (data) => {
    // Handle answer from support
  };

  const handleIceCandidate = (data) => {
    // Handle ICE candidates
  };

  const getPriorityIcon = (priority) => {
    switch(priority?.toLowerCase()) {
      case 'high': return <FaExclamationTriangle style={{color: '#FF5630'}} />;
      case 'medium': return <FaExclamationTriangle style={{color: '#FFAB00'}} />;
      case 'low': return <FaExclamationTriangle style={{color: '#36B37E'}} />;
      default: return <FaExclamationTriangle style={{color: '#6B778C'}} />;
    }
  };

  const filteredTickets = tickets.filter(ticket =>
    ticket.subject?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.from?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ticket.assignedName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    `LUX-${ticket.id}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleRemoteControlCommand = (command) => {
    console.log('Received remote control command:', command);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (loginData.username === 'Admin' && loginData.password === '#Admin123') {
      setIsAuthenticated(true);
      setLoginError('');
    } else {
      setLoginError('Invalid username or password');
    }
  };

  const renderPage = () => {
    switch(currentPage) {
      case 'dashboard':
        return renderDashboard();
      case 'tickets':
        return renderTickets();
      case 'users':
        return renderUsers();
      case 'analytics':
        return renderAnalytics();
      case 'settings':
        return renderSettings();
      default:
        return renderDashboard();
    }
  };

  const renderDashboard = () => (
    <div className="dashboard">
      <h2>Dashboard</h2>
      <div className="stats-grid">
        <div className="stat-card">
          <FaList className="stat-icon" />
          <div>
            <h3>{tickets.length}</h3>
            <p>Total Tickets</p>
          </div>
        </div>
        <div className="stat-card">
          <FaClock className="stat-icon" />
          <div>
            <h3>{tickets.filter(t => t.status === 'open').length}</h3>
            <p>Open Tickets</p>
          </div>
        </div>
        <div className="stat-card">
          <FaPlay className="stat-icon" />
          <div>
            <h3>{tickets.filter(t => t.status === 'in-progress').length}</h3>
            <p>In Progress</p>
          </div>
        </div>
        <div className="stat-card">
          <FaCheckCircle className="stat-icon" />
          <div>
            <h3>{tickets.filter(t => t.status === 'resolved').length}</h3>
            <p>Resolved</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderTickets = () => (
    <div className="tickets-page">
      <div className="page-header">
        <h2>Support Tickets</h2>
        <button className="btn-primary" onClick={() => setShowForm(true)}>
          <FaPlus /> New Ticket
        </button>
      </div>
      <div className="search-bar">
        <FaSearch />
        <input
          type="text"
          placeholder="Search tickets..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="tickets-list">
        {filteredTickets.map(ticket => (
          <div key={ticket.id} className="ticket-card">
            <div className="ticket-header">
              <span className="ticket-id">LUX-{ticket.id}</span>
              {getPriorityIcon(ticket.priority)}
            </div>
            <h3>{ticket.subject}</h3>
            <p>From: {ticket.from}</p>
            <p>Assigned to: {ticket.assignedTo}</p>
            <div className="ticket-actions">
              <button onClick={() => updateTicket(ticket.id, { status: 'in-progress' })}>
                <FaPlay /> Start Working
              </button>
              <button onClick={() => startScreenShare(ticket.id)}>
                <FaDesktop /> Screen Share
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderUsers = () => (
    <div className="users-page">
      <div className="page-header">
        <h2>Users</h2>
        <button className="btn-primary" onClick={() => setShowUserForm(true)}>
          <FaPlus /> Add User
        </button>
      </div>
      <div className="users-grid">
        {users.map(user => (
          <div key={user.id} className="user-card">
            <FaUser className="user-avatar" />
            <h3>{user.name}</h3>
            <p>{user.email}</p>
            <span className="user-role">{user.role}</span>
          </div>
        ))}
      </div>
    </div>
  );

  const renderAnalytics = () => (
    <div className="analytics-page">
      <h2>Analytics</h2>
      <p>Analytics dashboard coming soon...</p>
    </div>
  );

  const renderSettings = () => (
    <div className="settings-page">
      <h2>Settings</h2>
      <p>Settings panel coming soon...</p>
    </div>
  );

  if (!isAuthenticated) {
    return (
      <div className="login-container">
        <div className="login-form">
          <h2>Lux Ticketing Tools</h2>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={loginData.username}
              onChange={(e) => setLoginData({...loginData, username: e.target.value})}
            />
            <input
              type="password"
              placeholder="Password"
              value={loginData.password}
              onChange={(e) => setLoginData({...loginData, password: e.target.value})}
            />
            <button type="submit">Login</button>
            {loginError && <p className="error">{loginError}</p>}
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <nav className="navbar">
        <div className="nav-left">
          <button className="hamburger" onClick={() => setSidebarOpen(!sidebarOpen)}>
            <FaBars />
          </button>
          <h1>Lux Ticketing Tools</h1>
        </div>
        <div className="nav-right">
          <FaUser className="user-icon" />
          <span>Admin</span>
          <button className="logout-btn" onClick={() => setIsAuthenticated(false)}>
            <FaSignOutAlt />
          </button>
        </div>
      </nav>

      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-menu">
          <div 
            className={`menu-item ${currentPage === 'dashboard' ? 'active' : ''}`}
            onClick={() => {setCurrentPage('dashboard'); setSidebarOpen(false);}}
          >
            <FaTachometerAlt /> Dashboard
          </div>
          <div 
            className={`menu-item ${currentPage === 'tickets' ? 'active' : ''}`}
            onClick={() => {setCurrentPage('tickets'); setSidebarOpen(false);}}
          >
            <FaList /> Tickets
          </div>
          <div 
            className={`menu-item ${currentPage === 'users' ? 'active' : ''}`}
            onClick={() => {setCurrentPage('users'); setSidebarOpen(false);}}
          >
            <FaUsers /> Users
          </div>
          <div 
            className={`menu-item ${currentPage === 'analytics' ? 'active' : ''}`}
            onClick={() => {setCurrentPage('analytics'); setSidebarOpen(false);}}
          >
            <FaChartBar /> Analytics
          </div>
          <div 
            className={`menu-item ${currentPage === 'settings' ? 'active' : ''}`}
            onClick={() => {setCurrentPage('settings'); setSidebarOpen(false);}}
          >
            <FaCog /> Settings
          </div>
        </div>
      </div>

      <main className={`main-content ${sidebarOpen ? 'shifted' : ''}`}>
        {renderPage()}
      </main>

      {showForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Create New Ticket</h3>
            <form onSubmit={createTicket}>
              <input
                type="text"
                placeholder="Subject"
                value={formData.subject}
                onChange={(e) => setFormData({...formData, subject: e.target.value})}
                required
              />
              <textarea
                placeholder="Description"
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                required
              />
              <input
                type="email"
                placeholder="From Email"
                value={formData.from}
                onChange={(e) => setFormData({...formData, from: e.target.value})}
                required
              />
              <div className="modal-actions">
                <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
                <button type="submit">Create Ticket</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showUserForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Add New User</h3>
            <form onSubmit={createUser}>
              <input
                type="text"
                placeholder="Name"
                value={userFormData.name}
                onChange={(e) => setUserFormData({...userFormData, name: e.target.value})}
                required
              />
              <input
                type="email"
                placeholder="Email"
                value={userFormData.email}
                onChange={(e) => setUserFormData({...userFormData, email: e.target.value})}
                required
              />
              <select
                value={userFormData.role}
                onChange={(e) => setUserFormData({...userFormData, role: e.target.value})}
                required
              >
                <option value="">Select Role</option>
                <option value="IT Support">IT Support</option>
                <option value="SAP Support">SAP Support</option>
                <option value="Admin">Admin</option>
              </select>
              <input
                type="text"
                placeholder="Department"
                value={userFormData.department}
                onChange={(e) => setUserFormData({...userFormData, department: e.target.value})}
                required
              />
              <div className="modal-actions">
                <button type="button" onClick={() => setShowUserForm(false)}>Cancel</button>
                <button type="submit">Add User</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
